package com.jerry.financecrawler.job.howbuy;import com.jerry.financecrawler.commons.CommonsCharset;import com.jerry.financecrawler.commons.CommonsUrl;import com.jerry.financecrawler.job.QuartzJob;import com.jerry.financecrawler.save.SaveData;import com.jerry.financecrawler.translate.howbuy.HtmlToFundProductVo;import com.jerry.financecrawler.visitor.HtmlRequest;import com.jerry.financecrawler.vo.FundProductTotalVo;import com.jerry.financecrawler.vo.FundProductVo;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import javax.annotation.Resource;import java.util.List;/** * Created by Jerry on 2015/9/17. */public class FundProductJob implements QuartzJob {    /**     * Logger     */    private static final Logger log = LoggerFactory.getLogger(FundProductJob.class);    private static final String baseUrl = CommonsUrl.HOW_BUY_FINANCE_URL;    private static final String charset = CommonsCharset.UTF_8;    @Resource    private HtmlRequest htmlRequest;    @Resource    private HtmlToFundProductVo htmlToFundProductVo;    @Resource    private SaveData saveData;    @Override    public void execute() {        log.info("howbuy 基金产品采集服务启动");        try {            int index = 1;            int pageNum = 100;            FundProductTotalVo fundProductTotalVo = getHtmlData(index, pageNum, 0);            if (fundProductTotalVo != null) {                int allPages = fundProductTotalVo.getAllPages();                index++;                while (index <= allPages) {                    getHtmlData(index, pageNum, allPages);                    index++;                }            }        } catch (Exception ex) {            ex.printStackTrace();            log.error("dealing FundProductJob data", ex);        }        log.info("howbuy 基金产品采集服务完成");    }    private FundProductTotalVo getHtmlData(int index, int pageNum, int allpages) throws Exception {        String url = CommonsUrl.getUrl(baseUrl, index, pageNum, 0);        String html = htmlRequest.getHtmlData(url, charset);        FundProductTotalVo fundProductTotalVo = null;        if (!html.equals("")) {            fundProductTotalVo = htmlToFundProductVo.parseToFundProductData(html);            if (fundProductTotalVo != null) {                List<FundProductVo> fundProductVoList = fundProductTotalVo.getDatas();                saveData.saveFundProductData(fundProductVoList);            }        }        return fundProductTotalVo;    }}